/**
 * Bootstrap Responsive Table Dropdown Plugin
 *
 * Currently if you attempt to use the bootstrap dropdown
 * plugin within a table wrapped in the .table-responsive
 * class the dropdown menu will be 'cut-off' (a portion of
 * the menu will be hidden).
 *
 * This plugin fixes this problem using fixed positioning
 * rather than absolute positioning for the elements that
 * need rescuing.
 *
 * @author Jake Wilkinson <jake.wilkinson@backslash.co.nz>
 * @version 1.0.0
 * @license MIT
 */
!function($,o,n){$(n).on("ready",function(){var n=$(o);$(".table-responsive [data-toggle=dropdown]").each(function(){var o=$(this),e=o.parent(),r=e.find(".dropdown-menu");o.parent().on("show.bs.dropdown.bs-responsive-table-dropdown",function(){var o=e.position();r.css({top:o.top-n.scrollTop()+(e.height()*2),left:o.left-n.scrollLeft()-r.width()+e.width(),right:"inherit",position:"fixed"})})}),n.on("scroll.bs-responsive-table-dropdown resize.bs-responsive-table-dropdown",function(){var o=$(".open > [data-toggle=dropdown]"),n=o.parent(),e=n.find(".dropdown-menu");n.removeClass("open"),e.prop("aria-expanded",!1),o.blur()})})}(jQuery,window,document);